INSERT INTO faqs (question, answer, category_id) VALUES 
('¿Cómo puedo cambiar mi contraseña?', 'Para cambiar tu contraseña, ve a tu perfil y selecciona "Cambiar Contraseña". Recibirás un correo con las instrucciones.', 1),
('¿Cuánto tiempo tarda en responder un técnico?', 'Nuestro tiempo medio de respuesta es de menos de 2 horas para tickets críticos y 24 horas para consultas generales.', 3),
('¿Cómo adjunto archivos a un ticket?', 'Actualmente estamos implementando esta funcionalidad. Por ahora, puedes enviar links a documentos compartidos en el chat.', 3),
('¿Qué hago si mi ticket está marcado como Atrasado?', 'No te preocupes, el sistema alerta automáticamente a los supervisores cuando un ticket supera las 24 horas sin resolución.', 3),
('¿Puedo reabrir un ticket cerrado?', 'Sí, simplemente envía un nuevo mensaje en el chat del ticket y el estado cambiará automáticamente a En Progreso.', 1);
